    import {LLMRoleType} from '@/types/llm';
    import { MetaData } from '@/types/meta';
    import { nanoid } from '@/utils/uuid';

interface AddMessage {
id?: string;
message: string;
meta?: MetaData;
parentId?: string;
quotaId?: string;
role: LLMRoleType;
type: 'addMessage';
}

interface DeleteMessage {
id: string;
type: 'deleteMessage';
}

interface ResetMessages {
topicId?: string;
type: 'resetMessages';
}

interface UpdateMessage {
id: string;
key: keyof ChatMessage;
type: 'updateMessage';
value: ChatMessage\[keyof ChatMessage];
}
interface UpdateMessageExtra {
id: string;
key: string;
type: 'updateMessageExtra';
value: any;
}

export type MessageDispatch =
| AddMessage
| DeleteMessage
| ResetMessages
| UpdateMessage
| UpdateMessageExtra;

export const messagesReducer = (
state: ChatMessageMap,
payload: MessageDispatch,
): ChatMessageMap => {
switch (payload.type) {
case 'addMessage': {
return produce(state, (draftState) => {
const mid = payload.id || nanoid();

        draftState[mid] = {
          content: payload.message,
          createAt: Date.now(),
          id: mid,
          meta: payload.meta || {},
          parentId: payload.parentId,
          quotaId: payload.quotaId,
          role: payload.role,
          updateAt: Date.now(),
        };
      });
    }

    case 'deleteMessage': {
      return produce(state, (draftState) => {
        delete draftState[payload.id];
      });
    }

    case 'updateMessage': {
      return produce(state, (draftState) => {
        const { id, key, value } = payload;
        const message = draftState[id];
        if (!message) return;

        // @ts-ignore
        message[key] = value;
        message.updateAt = Date.now();
      });
    }

    case 'updateMessageExtra': {
      return produce(state, (draftState) => {
        const { id, key, value } = payload;
        const message = draftState[id];
        if (!message) return;

        if (!message.extra) {
          message.extra = { [key]: value } as any;
        } else {
          message.extra[key] = value;
        }

        message.updateAt = Date.now();
      });
    }

    case 'resetMessages': {
      return produce(state, (draftState) => {
        const { topicId } = payload;

        const messages = Object.values(draftState).filter((message) => {
          // –ê–∫–æ –Ω—è–º–∞ topicId, –∑–Ω–∞—á–∏ –µ –∑–∞ –∏–∑—á–∏—Å—Ç–≤–∞–Ω–µ –Ω–∞ —Å—ä–æ–±—â–µ–Ω–∏—è—Ç–∞ –≤ –ø–æ–¥—Ä–∞–∑–±–∏—Ä–∞—â–∏—è —Å–µ —Ä–∞–∑–≥–æ–≤–æ—Ä
          if (!topicId) return !message.topicId;

          return message.topicId === topicId;
        });

        // –ò–∑—Ç—Ä–∏–≤–∞–Ω–µ –Ω–∞ –Ω–∞–º–µ—Ä–µ–Ω–∏—Ç–µ —Å—ä–æ–±—â–µ–Ω–∏—è
        for (const message of messages) {
          delete draftState[message.id];
        }
      });
    }

    default: {
      throw new Error('–¢–∏–ø—ä—Ç –Ω–µ –µ —Ä–µ–∞–ª–∏–∑–∏—Ä–∞–Ω, –º–æ–ª—è –ø—Ä–æ–≤–µ—Ä–µ—Ç–µ —Ä–µ–¥—É–∫—Ç–æ—Ä–∞');
    }

}
};

