  code: number;
  msg: string;
  data: Array<{
    /** ID przestrzeni */
    spaceId: number;
    /** ID pliku */
    fileId: string;
    /** Nazwa pliku */
    fileName: string;
    /** Typ pliku */
    type: Type;
    /** ID wza nadrzdnego, gdy nadrzdny jest pusty, to "" */
